function NewsInfo({ data, onEditHandler, onDeleteHandler }) {

    const {author, title, description, url, source, image, category, language, country, published_at} = data;

    const onClickEditHandler = () => {
        onEditHandler(data);
    }

    const onClickDeleteHandler = () => {
        onDeleteHandler(data);
    }

    return (
        <>
            <div>
                <div className="card">
                    <div className="card-header">
                        <h4>{title}</h4>
                    </div>
                    <div className="card-body">
                        <div>
                            Author: {author}
                        </div>
                        <div>
                            Description: {description}
                        </div>

                    </div>
                    <div className="card-footer headCardFooter">
                        <button className="btn btn-primary">Read More..</button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default NewsInfo;
