function NewsInfo({ data, onEditHandler, onDeleteHandler }) {

    const { author, title, description, url, image, publishedAt, content } = data;

    console.log(author);

    //const onClickEditHandler = () => {
    //   onEditHandler(data);
    //}

    //const onClickDeleteHandler = () => {
    //    onDeleteHandler(data);
    //}
    const imagePlaceholder = () => {
        let checkImage = {image};

        if(checkImage != 'null'){
            return(image);
        }
        else{
            let defaultImage = '"/components/favlogo.png"';
            console.log(defaultImage);
            return(defaultImage);
        }
    }

    return (
        <>
            <div className="p-2">
                <div className="card-columns">
                    <div className="card">
                        <img className="card-img-top" src="components/favlogo.png" alt="Card image cap" />
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{description}</p>
                        </div>
                        <footer className="blockquote-footer p-2">
                            <small className="text-muted">
                                {author} |
                                Published at: {publishedAt}
                            </small>
                        </footer>

                    </div>
                </div>
            </div>
        </>
    );
}

export default NewsInfo;
