function NewsInfo({ articles, onEditHandler, onDeleteHandler }) {

    const { author, title, description, url, urlToImage, publishedAt, content } = articles;

    console.log(author);

    //const onClickEditHandler = () => {
    //   onEditHandler(data);
    //}

    //const onClickDeleteHandler = () => {
    //    onDeleteHandler(data);
    //}

    function imagePlaceholder() {
        let checkImage = {urlToImage};

        if(checkImage == 'null'){
            return('../public/assets/logo.png')
        }
    }

    return (
        <>
            <div className="p-2">

                <div className="card-columns">
                    <div className="card">
                        <img className="card-img-top" src={imagePlaceholder()} alt="Card image cap" />
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{description}</p>
                        </div>
                        <footer className="blockquote-footer p-2">
                            <small className="text-muted">
                                {author} |
                                Published at: {publishedAt}
                            </small>
                        </footer>

                    </div>
                </div>
            </div>
        </>
    );
}

export default NewsInfo;
